import groovy.json.JsonSlurper
import com.eviware.soapui.model.testsuite.TestRunner.Status

def response = messageExchange.response.responseContent
def resp1 = new JsonSlurper().parseText(response)
//Get the access token from response
access_token = resp1.data.access_token
log.info "Access Token generated: " +access_token
//save access token at test suite level
context.testCase.testSuite.setPropertyValue('access_token',access_token)

import groovy.json.JsonSlurper

def response = messageExchange.response.responseContent
def resp1 = new JsonSlurper().parseText(response)
//Get the newly created customer id  from response
cust_id = resp1.data.id
log.info "Customer created: " +cust_id
//save newly created customer id in Properties
context.testCase.testSteps["AllProperties"].setPropertyValue('CustomerId',cust_id.toString())

import groovy.json.JsonSlurper
def response = messageExchange.response.responseContent def resp1 = new JsonSlurper().parseText(response) //Get customer details from response cust_grp_id = resp1.data.customer_group_id cust_fname = resp1.data.firstname cust_lname = resp1.data.lastname cust_email = resp1.data.email log.info "Customer Name: " +cust_fname +" "+cust_lname +" and Customer Group Id is: " +cust_grp_id +" Email" +cust_email
//save customer details in Properties context.testCase.testSteps["AllProperties"].setPropertyValue('CustomerGroupId',cust_grp_id.toString()) context.testCase.testSteps["AllProperties"].setPropertyValue('CustFName',cust_fname) context.testCase.testSteps["AllProperties"].setPropertyValue('CustLName',cust_lname) context.testCase.testSteps["AllProperties"].setPropertyValue('CustEmail',cust_email)

