import groovy.json.JsonSlurper
def response = messageExchange.response.responseContent
def resp1 = new JsonSlurper().parseText(response)
//Get the product_option_id & from response
product_option_id = resp1.data.options[0].product_option_id
product_option_value_id = resp1.data.options.option_value[0].product_option_value_id[0]
log.info "Size parameters to create Order: " +product_option_id +" "+ product_option_value_id
//save Size parameters at test suite level
context.testCase.testSteps["AllProperties"].setPropertyValue('product_option_id',product_option_id.toString())
context.testCase.testSteps["AllProperties"].setPropertyValue('product_option_value_id',product_option_value_id.toString())

