import groovy.json.JsonSlurper
def response = messageExchange.response.responseContent
def resp1 = new JsonSlurper().parseText(response)
//Get the status  from response
status = resp1.data[0].status
log.info "Updated status: "+status

ReqStatus = context.testCase.testSteps["AllProperties"].getPropertyValue('UpdateStatus')
log.info "Requested status: "+ReqStatus
//compare updated status is same as requested status
if (status==ReqStatus)
{
	log.info "Status is Successfully updated to " +status
}

